import React, { useState } from 'react';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import authService from '../services/authService';

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();
  const location = useLocation() as any;
  const from = location.state?.from || '/dashboard';

  const onSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);
    setError(null);
    try {
      await authService.login({ email, password });
      navigate(from, { replace: true });
    } catch (err: any) {
      setError(err?.error || err?.message || 'Login failed');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="page">
      <h2>Login</h2>
      <div className="demo-info">
        <strong>Demo mode</strong>
        If you enabled mock API, you can sign in with:
        <div>Email: {process.env.REACT_APP_DEMO_EMAIL || 'demo@example.com'}</div>
        <div>Password: {process.env.REACT_APP_DEMO_PASSWORD || 'demo1234'}</div>
      </div>
      <form onSubmit={onSubmit} style={{ maxWidth: 360 }}>
        <div className="form-row">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="you@example.com"
          />
        </div>
        <div className="form-row">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="••••••••"
          />
        </div>
        {error && (
          <div className="error" role="alert">
            {error}
          </div>
        )}
        <button type="submit" disabled={submitting}>
          {submitting ? 'Signing in...' : 'Sign in'}
        </button>
      </form>
      <p style={{ marginTop: 12 }}>
        No account? <Link to="/">Go back Home</Link>
      </p>
    </div>
  );
};

export default LoginPage;
