import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Smart Medicine Refill System</h1>
        </header>
        
        <main className="App-main">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/dashboard" element={<DashboardPage />} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

// Temporary placeholder components
const HomePage: React.FC = () => (
  <div className="page">
    <h2>Welcome to Smart Medicine Refill System</h2>
    <p>Never miss your medication refills again!</p>
    <div className="actions">
      <button onClick={() => window.location.href = '/login'}>Login</button>
      <button onClick={() => window.location.href = '/dashboard'}>Dashboard</button>
    </div>
  </div>
);

const LoginPage: React.FC = () => (
  <div className="page">
    <h2>Login</h2>
    <p>Login page will be implemented here</p>
    <button onClick={() => window.location.href = '/'}>Back to Home</button>
  </div>
);

const DashboardPage: React.FC = () => (
  <div className="page">
    <h2>Dashboard</h2>
    <p>Medicine dashboard will be implemented here</p>
    <button onClick={() => window.location.href = '/'}>Back to Home</button>
  </div>
);

export default App;
